import { css, unsafeCSS } from 'lit-element';
import { colors } from './bbva-foundations-styles-colors.js';

const setStylesFocus = (selector = '', hostAttribute, notProps, props) => {
  const result = `
     :host(:not(${hostAttribute})) ${selector} {
          ${notProps}
      }
      :host(${hostAttribute}) ${selector} {
          ${props}
      }
  `;
  return css`
    ${unsafeCSS(result)}
  `;
};

export const bbvaFoundationsStylesFocus = selector =>
  setStylesFocus(
    selector,
    '[focus-visible]',
    `
    outline: none;
  `,
    `
    outline: ${colors.primaryMedium} solid 1px;
    outline-offset: 4px;
    @apply --bbva-foundations-styles-focus;
  `,
  );
