import { setComponentSharedStyles, } from '@cells-components/cells-lit-helpers/cells-lit-helpers.js';
import { css, } from 'lit-element';

setComponentSharedStyles('spinner-styles', css`
  /**
   * Required DOM
   *
   * <div class="loader-container ball-chasing">
   *   <div class="loader">
   *     <div class="ball-1"></div>
   *     <div class="ball-2"></div>
   *   </div>
   * </div>
   */
  @-webkit-keyframes rotate {
    100% {
      -webkit-transform: rotate(360deg);
              transform: rotate(360deg); } }

  @keyframes rotate {
    100% {
      -webkit-transform: rotate(360deg);
              transform: rotate(360deg); } }

  @-webkit-keyframes chasingBallBounce {
    50% {
      -webkit-transform: scale(0);
              transform: scale(0);
      opacity: 0; } }

  @keyframes chasingBallBounce {
    50% {
      -webkit-transform: scale(0);
              transform: scale(0);
      opacity: 0; } }

  .ball-chasing .loader {
    width: 60px;
    height: 60px;
    -webkit-animation: rotate 1s linear infinite;
            animation: rotate 1s linear infinite; }

  .ball-1 {
    background-color: var(--cells-demo-spinner-color-1, #fc6a7c); }

  .ball-2 {
    background-color: var(--cells-demo-spinner-color-2, #1cc3df); }

  .ball-chasing .loader .ball-1,
  .ball-chasing .loader .ball-2 {
    position: absolute;
    top: 0;
    width: 60%;
    height: 60%;
    border-radius: 50%;
    -webkit-animation: chasingBallBounce 1.5s ease-in-out infinite;
            animation: chasingBallBounce 1.5s ease-in-out infinite; }

  .ball-chasing .loader .ball-2 {
    top: auto;
    bottom: 0;
    -webkit-animation-delay: -1s;
            animation-delay: -1s; }

  .loader-container .loader {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    margin: auto; }
`);
