/**
* Returns the deepest active element.
*
* @param {Node} element - The element to search.
* @returns {HTMLElement} - Active element
*/
export function deepActiveElement(element) {
  if (!element || element instanceof Element === false) {
    element = document.body;
  }

  if (element.shadowRoot) {
    while (element.shadowRoot && element.shadowRoot.activeElement) {
      element = element.shadowRoot.activeElement;
    }
  }

  return element;
}
