import * as foundations from '@bbva-web-components/bbva-foundations-styles';
import { setDocumentCustomStyles } from '@cells-components/cells-lit-helpers/cells-lit-helpers.js';
import { css, unsafeCSS } from 'lit-element';

setDocumentCustomStyles(css`
  #iframeBody {
    margin: 0;
  }
  ul {
    list-style: none;
    margin: 0;
    padding: 0.5em;
  }

  li {
    display: flex;
    justify-content: space-evenly;
    margin-bottom: 1rem;
  }

  div {
    padding: 0.5em;
  }

  div:nth-child(n + 3) {
    background-color: ${unsafeCSS(foundations.backgroundColors.dark)};
  }

  [ambient^='light'] {
    background-color: ${unsafeCSS(foundations.colors.primaryCoreLightened)};
  }

  [ambient^='dark'] {
    background-color: ${unsafeCSS(foundations.colors.primaryCore)};
    color: ${unsafeCSS(foundations.colors.primaryCoreLightened)};
  }
`);
