import { css, unsafeCSS } from 'lit-element';
import '../elements/bbva-core-dom-module.js';

const bbvaCoreDomModule = customElements.get('bbva-core-dom-module');

export const getComponentSharedStyles = id => {
  const domModule = bbvaCoreDomModule.import(id);
  if (domModule && domModule.modulesStyles[id]) {
    const onlyCSSResult = domModule.modulesStyles[id].filter(
      cssResultObject => cssResultObject.cssText,
    );
    const onlyUnsafeCssResult = unsafeCSS(onlyCSSResult.join(''));
    return css`
      ${onlyUnsafeCssResult}
    `;
  }
  return css``;
};
