import { css, unsafeCSS } from 'lit-element';
import * as foundations from '@bbva-web-components/bbva-foundations-styles';

export default css`[ambient^=dark] {
  --bbva-form-select-bg-color: #0A5393;
  --bbva-form-select-border-color: var(--colorsPrimaryCoreLightened, ${unsafeCSS(foundations.colors.primaryCoreLightened)});
  --bbva-form-select-readonly-border-color: var(--colorsSecondary100, ${unsafeCSS(foundations.colors.secondary100)});
  --bbva-form-select-button-color: var(--colorsPrimaryCoreLightened, ${unsafeCSS(foundations.colors.primaryCoreLightened)});
  --bbva-form-select-readonly-button-color: var(--colorsSecondary300, ${unsafeCSS(foundations.colors.secondary300)});
  --bbva-form-select-label-color: var(--colorsPrimaryCoreLightened, ${unsafeCSS(foundations.colors.primaryCoreLightened)});
  --bbva-form-select-filled-label-color: var(--colorsSecondary100, ${unsafeCSS(foundations.colors.secondary100)});
  --bbva-form-select-readonly-label-color: var(--colorsSecondary300, ${unsafeCSS(foundations.colors.secondary300)});
  --bbva-form-select-icon-color: var(--colorsPrimaryCoreLightened, ${unsafeCSS(foundations.colors.primaryCoreLightened)});
  --bbva-form-select-readonly-icon-color: var(--colorsSecondary100, ${unsafeCSS(foundations.colors.secondary100)});
  --bbva-form-select-message-color: var(--colorsPrimaryCoreLightened, ${unsafeCSS(foundations.colors.primaryCoreLightened)});
  --bbva-form-select-message-icon-color: var(--colorsPrimaryCoreLightened, ${unsafeCSS(foundations.colors.primaryCoreLightened)});
  --bbva-form-select-info-message-color: var(--colorsPrimaryCoreLightened, ${unsafeCSS(foundations.colors.primaryCoreLightened)});
  --bbva-form-select-info-message-icon-color: var(--colorsPrimaryCoreLightened, ${unsafeCSS(foundations.colors.primaryCoreLightened)});
  --bbva-form-select-invalid-bg-color: var(--colorsTertiaryType5Lightened, ${unsafeCSS(foundations.colors.tertiaryType5Lightened)});
  --bbva-form-select-invalid-border-color: var(--colorsTertiaryType5Medium, ${unsafeCSS(foundations.colors.tertiaryType5Medium)});
  --bbva-form-select-invalid-label-color: var(--colorsTertiaryType1Dark, ${unsafeCSS(foundations.colors.tertiaryType1Dark)});
  --bbva-form-select-invalid-icon-color: var(--colorsTertiaryType1Dark, ${unsafeCSS(foundations.colors.tertiaryType1Dark)}); }

[ambient=dark150] {
  --bbva-form-select-bg-color: var(--colorsPrimaryMedium, ${unsafeCSS(foundations.colors.primaryMedium)}); }

[ambient=dark300] {
  --bbva-form-select-bg-color: var(--colorsPrimaryCore, ${unsafeCSS(foundations.colors.primaryCore)}); }
`;