import { html, fixture, assert, fixtureCleanup } from '@open-wc/testing';

import '../bbva-list-bullet.js';

suite('bbva-list-bullet', () => {
  let el;

  teardown(() => fixtureCleanup());

  setup(async () => {
    el = await fixture(
      html`
        <bbva-list-bullet></bbva-list-bullet>
      `,
    );
    await el.updateComplete;
  });

  test('it has the icon `coronita:bullet1 by default`', () => {
    assert.equal(el.icon, 'coronita:bullet1');
  });

  test('it sets icon-size-24 on icon', () => {
    assert.equal(el.iconSize, '24');
  });
  test('a11y', () => assert.isAccessible(el));

  test('SHADOW DOM - Structure test', () => {
    assert.shadowDom.equalSnapshot(el);
  });

  test('LIGHT DOM - Structure test', () => {
    assert.lightDom.equalSnapshot(el);
  });
});
