/**
 * Returns the correct icon name
 * @param  {String} iconName camelCase String
 * @param  {String} iconSet  Name of iconset to use. Default bbva
 * @param  {String} divider  Divider string. Default value :
 * @return {String}          String to correct icon name. iconset:icon (bbva:close)
 */
export function getIconName(iconName, iconSet = 'bbva', divider = ':') {
  return iconSet + divider + iconName.replace(iconSet, '').toLowerCase();
}
