export const rafThrottle = (fn, thisArg) => {
  let frameId = null;
  let lastState = [];

  const run = context => () => {
    frameId = null;
    fn.apply(thisArg || context, lastState);
  };

  const throttled = function (...args) {
    lastState = args;
    if (frameId === null) {
      frameId = requestAnimationFrame(run(this));
    }
  };

  throttled.cancel = () => {
    cancelAnimationFrame(frameId);
    frameId = null;
  };

  return throttled;
};
