import { css, unsafeCSS} from 'lit-element';
import { setDocumentCustomStyles, } from '@cells-components/cells-lit-helpers';
import * as foundations from '@bbva-web-components/bbva-foundations-styles';

setDocumentCustomStyles(css`
  #iframeBody {
    margin: 0;
    padding: 1rem;
  }

  bbva-form-select {
    margin-bottom: 1.5rem;
    max-width: 300px;
  }

  .customization bbva-form-select {
    max-width: none;
    margin-bottom: 0;
  }

  .examples {
    padding: 1rem;
    display: flex;
    flex-direction: column;
    align-items: center;
  }

  .examples bbva-form-select {
    width: 100%;
    max-width: 300px;
  }

  [ambient^=light] {
    background-color: ${unsafeCSS(foundations.colors.primaryCoreLightened)};
  }

  [ambient^=dark] {
    background-color: ${unsafeCSS(foundations.colors.primaryCore)};
  }

  [ambient=light100] {
    background-color: ${unsafeCSS(foundations.colors.secondary100)};
  }

  [ambient=dark100] {
    background-color: ${unsafeCSS(foundations.colors.primaryMedium)};
  }

  [ambient=dark150] {
    background-color: ${unsafeCSS(foundations.colors.primaryCoreLight)};
  }

  [ambient=dark200] {
    background-color: ${unsafeCSS(foundations.colors.primaryCore)};
  }

  [ambient=dark300] {
    background-color: ${unsafeCSS(foundations.colors.primaryCoreDark)};
  }

  [ambient=dark400] {
    background-color: ${unsafeCSS(foundations.colors.primaryCoreDarkened)};
  }

  @media (max-width: 768px) {
    *, *:before, *:after {
      -webkit-tap-highlight-color: transparent;
    }
  }
`);
